mapper.select:
select SQL_CALC_FOUND_ROWS h.id, h.title, c.name as course, h.lastCommitDate, h.createDate, h.briefInfo, IFNULL(vc.count,0) count from homework h JOIN course c on h.course=c.id LEFT JOIN v_commit_count vc on h.id=vc.hid
c.id=#{course}
and h.title like '%' #{title} '%'
order by h.id desc LIMIT #{skipCount},#{limit}

select count(*)
from commit_homework
where hid=#{hid} and uid=#{uid};

select SQL_CALC_FOUND_ROWS h.id, h.title, c.name as course, h.lastCommitDate, h.createDate, h.briefInfo from homework h JOIN course c on h.course=c.id
(h.id not in(SELECT hid from commit_homework WHERE uid=#{uid}))
and c.id=#{course}
and h.title like '%' #{title} '%'
order by h.id desc LIMIT #{skipCount},#{limit}
